.dashboard {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 90vh;

    .header {
      display: flex;
      justify-content: space-between;

      border-bottom: 4px solid $primary;
      max-width: 1200px;
      width: 100%;

      .left {}

      .right {
        display: flex;
        align-items: center;

        .filter {
          display: flex;
          margin-right: 10px;

          .tag {
            margin-right: 5px;
          }
        }

        .nav {
          display: flex;

          .ball {
            width: 20px;
            height: 20px;
            background-color: $primary;
            margin-right: 5px;
            border-radius: 50%;

            &.active {
              border: 1px solid black;
              background-color: white !important;
            }
          }
        }
      }
    }

    .entries {
      display: flex;
      flex-direction: row;
      flex-wrap: wrap;
      justify-content: space-between;

      margin-top: 30px;
      max-width: 1200px;
      width: 100%;

      .entry-wrapper {
        display: flex;
        flex-direction: column;
        align-items: center;

        &a {
          text-decoration: none;
        }

        .date {
          margin-bottom: 10px;
        }
      }
    }
  }

